class Solution:
    def longestNiceSubstring(self, s: str) -> str:

        def dp(i, j):
            if i > j:
                return ''
            count = set(list(s[i:j+1]))
            for k,c in enumerate(s[i:j+1]):
                if not (c.upper() in count and c.lower() in count):
                    break
            else:
                return s[i:j+1]
            return max([dp(i, i + k - 1), dp(i + k + 1, j)], key=len)

        n = len(s)
        return dp(0, n - 1)
