const longestNiceSubstring = function (s) {
  const isNice = function (str) {
    const set = [...new Set(str)].join('');
    return set.length == new Set(set.toLowerCase()).size * 2;
  }
  let len = s.length, ans = '';
  for (let i = 2; i <= len; i++) {
    let [l, r] = [0, i];
    while (r <= len) {
      let currSubstr = s.slice(l, r);
      if (isNice(currSubstr) && r - l > ans.length) {
        ans = currSubstr;
      }
      l++; r++;
    }
  }
  return ans;
};
