public class Solution {
	public string LongestNiceSubstring(string s) {
		int n = s.Length;
		var str = string.Empty;

		for (int i = 0; i < n; i++) {
			int xor1 = 0, xor2 = 0;
			for (int j = i; j < n; j++) {
				if(s[j] >= 'a' && s[j] <= 'z')
						xor1 |= (1 << (s[j] - 'a'));
					else
						xor2 |= (1 << (s[j] - 'A'));

				if((xor1 ^ xor2) == 0 && (j - i + 1) > str.Length)
					str = s.Substring(i, j - i + 1);
			}
		}

		return str;
	}
}

