```
#define vvi vector<vector<int>>
class Solution {
public:
    string longestNiceSubstring(string s) {
        int n = s.length();
        vvi cntu(n+1,vector<int>(26,0)); //cntu[i][j] => count of j'th uppercase character in [0->i]
        vvi cntl(n+1,vector<int>(26,0));//cntu[i][j] => count of j'th lowercase character in [0->i]
        for(int i=0;i<n;i++){
            cntl[i+1]=cntl[i];
            cntu[i+1]=cntu[i];
            if(s[i]&32){//check for lowercase [ obv: lowercase letter have setbit in 6th position from right]
                int inx = s[i]-'a';
                cntl[i+1][inx]++;
            }else{
                int inx = s[i]-'A';
                cntu[i+1][inx]++;
            }
        }
        return helper(s,0,n-1,cntl,cntu);
    }
    string helper(string &str,int s,int e, vvi& cntl, vvi& cntu){
        if(s > e) return "";
        for(int i=s;i<=e;i++){
            int inx = 0;
            int c = 0;
            if(str[i]&32){ //if cur character is lowercase then check if it's uppercase is present in that range or not
                inx = str[i]-'a';
                c = cntu[e+1][inx]-cntu[s][inx];
            }else{
                inx = str[i]-'A';
                c = cntl[e+1][inx]-cntl[s][inx];
            }
            if(c==0){ //if the alter character is absent then result either in left part or in right part
                string left=helper(str,s,i-1,cntl,cntu);
                string right=helper(str,i+1,e,cntl,cntu);
                if(left.length() < right.length()) return right;
                return left;
            }
        }
        return str.substr(s,e-s+1);
    }
};
