    def is_nice(self, s: str) -> bool:
        mask = 0
        for ch in s:
            mask |= 1 << ord(ch)
        mask >>= 65
        diff = ord('a') - ord('A')
        for i in range(diff):
            if ((mask & 1) ^ ((mask >> diff) & 1)):
                return False
            mask >>= 1
        return True

    def longestNiceSubstring(self, s: str) -> str:
        mx = -1
        res = ""
        for i in range(len(s) - 1):
            for j in range(i + 1, len(s) + 1):
                sub = s[i:j]
                if self.is_nice(sub) and len(sub) > mx:
                    mx = len(sub)
                    res = sub
        return res

