class Solution {
public:
    string longestNiceSubstring(string a) {

        int n = a.size(),f,g;
        unordered_map<char,int> m;
        string ans ;
        char h ;
        for(int i=0;i<n;i++)
        {
            f = -1 ;                  // For max length of subsrting for current loop 
            g=0;                      // For determing substring is nice or not 
            for(int j=i;j<n;j++)
            {

                if(a[j]>92)           //  Opposite case alphabet
                    h = a[j]-32;
                else
                    h = a[j]+32;
                if(!m.count(a[j]))                  
                {
                    if(!m.count(h))   // If opposite case alphabet not present increase g 
                        g++;
                    else              // If opposite case alphabet present decrease g
                        g--;
                    m[a[j]]++;
                }

                if(g==0)             // If g = 0 means string is nice, update f for length of substring
                    f = j;

            }
            if(f!=-1&&ans.size()<f-i+1)  
            {
                ans = a.substr(i,f-i+1); // Updating ans string 
            }
            m.clear();              // Clear map for next loop

        }
        return ans;



    }
};
