class Solution:
    def longestNiceSubstring(self, s: str) -> str:
        if len(s) == 1:
            return ""
        if len(s) == 2:
            if s[0].swapcase() not in s:
                return ""
            else:
                return s
        dictt = {} #for storing nice seq with their length
        for i in range(2,len(s)+1): #checking each substring with a particular size
            seq = s[0:i]
            for j in range(i, len(s)+1):
                myset = set(seq) #set allow O(1) lookups
                count = 0
                for k in seq: #traversing through the seq to see if its nice
                    if k.swapcase() not in myset:
                        break
                    else:
                        count += 1
                if len(seq) == count:
				#here, seq is nice, hence we store in the dictionary
                    dictt[seq] = count
                if j != len(s):
                    seq = seq[1:] + str(s[j]) #sliding window
        if len(dictt) == 0:
            return ""
        else:
            maxi = max(dictt.values())
        for x in dictt.keys():
            if dictt[x] == maxi:
                return x
