class Solution {
public:
    string longestNiceSubstring(string s) {       
        int bestStartPos = 0;
        int bestLength = 0;

        for (int startPos = 0; startPos < s.size(); startPos++) {
            int lower = 0;
            int upper = 0;

            for (int pos = startPos; pos < s.size(); pos++) {
                if (islower(s[pos])) {
                    lower |= 1 << (s[pos]-'a');
                } else {
                    upper |= 1 << (s[pos]-'A');
                }

                if ((lower ^ upper) == 0) {
                    const int currLength = pos - startPos + 1;
                    if (currLength > bestLength) {
                        bestLength = currLength;    
                        bestStartPos = startPos;
                    }
                }
            }
        }

        return s.substr(bestStartPos, bestLength);
    }
};
