    def find_pivot(self,s,i,j):
        dic={s[k]:k for k in range(i,j+1)}
        for d in dic:
            if ord(d)>=97 and d.upper() not in dic:
                return False,dic[d]
            elif ord(d)>=65 and d.lower() not in dic:
