        if i>=j:
            return ''
        check,val=self.find_pivot(s,i,j)
        if check==True:
            return val
        return max(self.func(s,i,val-1),self.func(s,val+1,j),key=len)


    def longestNiceSubstring(self, s: str) -> str:
        return self.func(s,0,len(s)-1)

