class Solution {
     public String longestNiceSubstring(String s) {

        int i = 0, j = 0;
        int len = s.length();

        TreeMap<Integer, List<String>> map = new TreeMap<>();
        while (i < len) {
            String str = s.substring(i, j);
            if (isNice(str)) {
                int size = str.length();
                List<String> list = map.getOrDefault(size, new ArrayList<>());
                list.add(str);
                map.put(size, list);
            }
            j++;
            if (j > len) {
                i++;
                j = i;
            }
        }
        if (map.isEmpty()) {
            return "";
        }
        return map.lastEntry().getValue().get(0);

    }

    public boolean isNice(String str) {
        if (str.isEmpty()) {
            return false;
        }
        int[] upper = new int[26];
        int[] lower = new int[26];

        for (char c : str.toCharArray()) {
            if (Character.isLowerCase(c)) {
                lower[c - 'a']++;
            } else {
                upper[c - 'A']++;
            }
        }

        for (int i = 0; i < 26; i++) {

            if ((lower[i] > 0 && upper[i] == 0) || (lower[i] == 0 && upper[i] > 0)) {
                return false;
            }
        }
        return true;
    }
}
