public string LongestNiceSubstring(string s)
{
	string result = string.Empty;
	int maxLength = 0;
	int resultStart = 0;

	for (int start = 0; start < s.Length; start++)
	{
		int upper = 0, lower = 0;                
		for (int end = start; end < s.Length; end++)
		{   
			int length = end - start + 1;
			if(char.IsUpper(s[end]))
			{
				upper |= 1 << (s[end] - 'A');
			}

			else
			{
				lower |= 1 << (s[end] - 'a');
			}                    

			if ((upper ^ lower) == 0 && length > maxLength)
			{
				resultStart = start;
				maxLength = length;
			}
		}
	}

	result = s.Substring(resultStart, maxLength);
	return result;
}
