String helper(String s, int left, int right) {
	int lower = 0;
	int upper = 0;
	for (int i = left; i < right; i++) {
		char c = s.charAt(i);
		if (c > 'Z') {
			lower |= 1 << (c-'a');
		} else {
			upper |= 1 << (c-'A');
		}
	}
	for (int i = left; i < right; i++) {
		char c = s.charAt(i);
		int shift = c - (c > 'Z' ? 'a' : 'A');
		if ((lower & 1 << shift) == (upper & 1 << shift)) {
			continue;
		}
		String leftStr = helper(s, left, i);
		String rightStr = helper(s, i+1, right);
		if (leftStr.length() >= rightStr.length()) {
			return leftStr;
		} else {
			return rightStr;
		}

	}
	return s.substring(left, right);
}
