class Solution {

    private List< List<Integer> > allSubset = new ArrayList< List<Integer> >();
    private List< Integer > bag = new ArrayList<Integer>();

    public List<List<Integer>> subsets(int[] nums) {

        makeSubsetFrom( 0, nums );    

        return allSubset;
