#include <vector>

using namespace std;

class Solution {
public:
    vector<vector<int>> subsets(vector<int>& nums) {
        vector<vector<int>> pw_set;
        vector<int> subset;
        recurse(nums, subset, pw_set, 0);
        return pw_set;
    }
private:
    void recurse (vector<int>& nums, vector<int>& subset, vector<vector<int>>& pw_set, int i) {
        if (i >= nums.size()) {
            pw_set.push_back(subset);
            return;
        }
        subset.push_back(nums[i]);
        recurse(nums, subset, pw_set, i + 1);

        subset.pop_back();
        recurse(nums, subset, pw_set, i + 1);
    }
};
