// Runtime: 112 ms, faster than 21.68% of JavaScript online submissions for Subsets.
// Memory Usage: 44.2 MB, less than 35.31% of JavaScript online submissions for Subsets.
const subsets = nums => {
	const output = [];

	const backtrack = (idx, subset) => {
		output.push([...subset]);

    // for each of the current subset
    // recursively generate all possible subsets by increasing the position by one
		for (let i = idx; i < nums.length; i++) {
			subset.push(nums[i]);
			backtrack(i + 1, subset); // create next subset
			subset.pop(); // backtrack
		}
	};

	backtrack(0, []);
	return output;
};

