    def subsetsWithDup(self, nums: List[int]) -> List[List[int]]:
        def dfs(count, res, path):
            if not count:
                res.append(path)
                return
            x = list(count.keys())[0]
            x_freq = count[x]
            del count[x]
            for i in range(x_freq + 1):
                dfs(count, res, path + i * [x])
            count[x] = x_freq

        c = Counter(nums)
        res  = []
        dfs(c, res, [])
        return res
