class cmp
{
    public:
      bool operator()(node a, node b)
      {
          return a.z < b.z;
      }
};

class Solution {
public:
    double maxAverageRatio(vector<vector<int>>& classes, int extraStudents) {
        int n = classes.size();
        priority_queue<node, vector<node>, cmp> pq;
        for(auto x : classes)
        {
            double prev = x[0] / (double)x[1];
            double next = (x[0] + 1) / (double)(x[1] + 1);
            node nodes(x[0], x[1], next - prev);
            pq.push(nodes);
        }
        while(extraStudents--)
        {
            auto temp = pq.top();
            pq.pop();
            temp.x++;
            temp.y++;
            double prev = temp.x / (double)temp.y;
            double next = (temp.x + 1) / (double)(temp.y + 1);
            node nodes(temp.x, temp.y, next - prev);
            pq.push(nodes);
