class Solution {
public:
    double maxAverageRatio(vector<vector<int>>& classes, int extraStudents) {
        priority_queue<pair<double, pair<int, int> > > pq;
        for(auto &cls : classes) {
            int t = cls[1], p = cls[0];
            double add = (p + 1) * 1.0 / (t + 1) - (p * 1.0 / t);
            pq.push({add, {p, t}});
        }
        while(extraStudents--) {
            auto p = pq.top(); pq.pop();
            int &t = p.second.second;
            int &pa = p.second.first;
            ++t; ++pa;
            p.first = (pa + 1) * 1.0 / (t + 1) - pa * 1.0 / t;
            pq.push(p);
        }
        double res = 0;
        while(pq.size()) {
            res += pq.top().second.first * 1.0 / pq.top().second.second; pq.pop();
        }
        return res / classes.size();
    }
};
