class Solution {
    public double maxAverageRatio(int[][] classes, int extraStudents) {
        //max heap
        //here we are using Max Heap, whaich has maximum element in root
        PriorityQueue<Class> maxHeap = new PriorityQueue<Class>(Comparator.comparingDouble(o -> -o.gainPassRatio));


        for(int[] eachClass : classes){
            int pass = eachClass[0];
            int total = eachClass[1];

            maxHeap.add(new Class(pass, total,  getGainPassRatio(pass, total)));
        }


        while(extraStudents --> 0){
            //we are using Gready Approach 
            //get the class, which will have a maximum 
            //gain in passing ratio, if we add a student
            Class c =  maxHeap.remove();

            //this class will have maximum gain in passing ratio
            //so add 1 extra student (who always pass)
            c.pass = c.pass + 1;
            c.total = c.total + 1;
            c.gainPassRatio = getGainPassRatio(c.pass + 1, c.total + 1);

            maxHeap.add(c);
        }

        double passRatioSum = 0.0d;
        while(!maxHeap.isEmpty()){
            Class c =  maxHeap.remove();
            passRatioSum += ((double)c.pass / c.total);
        }


        return passRatioSum / classes.length;
    }

    private double getGainPassRatio(int pass, int total){
        double passRatio =  (double)pass / total;

        //add 1 student, 
        pass++;
        total++;

        double passRatioAfterAdding = (double)pass / total;

        //find gain in passing ratio, if we add 1 student
        return passRatioAfterAdding - passRatio; 
    }
}

class Class {
    int pass;
    int total;
    double gainPassRatio;

    public Class(int pass, int total, double gainPassRatio){
        this.pass = pass;
        this.total = total;
        this.gainPassRatio = gainPassRatio;
    }
}
