class Solution {
public:
    double maxAverageRatio(vector<vector<int>>& classes, int extraStudents) {
        priority_queue<pair<long double,pair<long long int,long long int>>>pq;
        for(auto it:classes)
        {
            long long int z=(it[1]);
            z=z*(it[1]+1);
            long double pt=1.0*(it[1]-it[0])/z;
            pq.push({pt,{it[0],it[1]}});
        }
        for(int i=0;i<extraStudents;i++)
        {
            long long int t1=pq.top().second.first+1;
            long long int t2=pq.top().second.second+1;
            long long int z=t2;
            z=z*(t2+1);
            long double g=1.0*(t2-t1)/z;
            pq.pop();
            pq.push({g,{t1,t2}});
        }  
        long double avg=0;
        while(!pq.empty())
        {
            avg+=1.0*(pq.top().second.first)/(pq.top().second.second);
            pq.pop();
        }
        return 1.0*avg/classes.size();
    }
};
