        double pp=0;
        while(extraStudents>0){
            int a=pq.top().first+1;
            int b=pq.top().second+1;
            pq.pop();
            pq.push({a,b});
            extraStudents--;
        }

        while(!pq.empty()){
           pp+=(pq.top().first/(double)pq.top().second);
           pq.pop();
        }
        return pp/(double)classes.size();
    }
};



