class Class{
    double pass;
    double total;
    double inc;
    public Class(int[] cl){
        pass = cl[0];
        total = cl[1];
        inc = (pass + 1) / (total + 1) - pass / total;
    }
    public void addExtraStudent(){
        pass += 1;
        total += 1;
        inc = (pass + 1) / (total + 1) - pass / total;
    }
}
