class Solution {
    public double maxAverageRatio(int[][] classes, int extraStudents) {
        PriorityQueue<double[]> pq=new PriorityQueue<double[]>((a,b)->a[2]-b[2]>0?-1:1);
        double sum = 0;
        for(int i = 0; i < classes.length; i++) {
            if(classes[i][0] != classes[i][1]) {
                double passes = classes[i][0];
                double total = classes[i][1];
                pq.offer(new double[]{passes, total, getPotentialIncrease(passes, total)});
            } else {
                sum++;
            }
        }
        if(sum == classes.length) {
            return 1;
        }

        while(!pq.isEmpty() && extraStudents-- > 0) {
		    // poll the max potential increase element everytime
            double[] potential = pq.poll();
			// apply 1 extra student count to this 
            potential[0] += 1;
            potential[1] += 1;
			// re-calculate the potential ratio increase for this element and put to pq again.
            pq.offer(new double[]{potential[0], potential[1], getPotentialIncrease(potential[0], potential[1])});
        }
		// poll all elements in qp and caclulate the sum of retios with max increases
        while(!pq.isEmpty()) {
            double[] current = pq.poll();
            sum+= current[0]/current[1];
        }
        return sum/classes.length;
    }

    private double getPotentialIncrease(double passes, double total) {
        return (passes + 1) / (total + 1)  - (passes / total);
    }
}
