```
class Solution {
    public double maxAverageRatio(int[][] classes, int extraStudents) {
        Comparator<int[]> comp = new Comparator<>() {
            @Override
            public int compare(int[] a, int[] b) {
                double aBefore = (double) a[0] / (double) a[1];
                double bBefore = (double) b[0] / (double) b[1];
                double aAfter  = (double) (a[0] + 1) / (double) (a[1] + 1);
                double bAfter  = (double) (b[0] + 1) / (double) (b[1] + 1);

                double dxOfA = aAfter - aBefore;
                double dxOfB = bAfter - bBefore;

                return Double.compare(dxOfB, dxOfA);
            }
        };

        PriorityQueue<int[]> queue = new PriorityQueue<>(comp);

        for(int[] arr : classes) {
            queue.add(arr);
        }

        while(extraStudents-- > 0) {
            int[] cur = queue.remove();
            cur[0]++; cur[1]++;
            queue.add(cur);
        }

        double sumOfRatios = 0;
        while(!queue.isEmpty()) {
            int[] cur = queue.remove();
            sumOfRatios += (double) cur[0] / (double) cur[1];
        }

        return sumOfRatios / (double) classes.length;
    }
}
