import CoreFoundation
class Solution {
    func maxAverageRatio(_ classes: [[Int]], _ extraStudents: Int) -> Double {
        let minHeap = CFBinaryHeap.minHeap()
        var pointer: UnsafeMutablePointer<[Int]> = .allocate(capacity: 1)
        for v in classes
        {
            pointer = .allocate(capacity: 1)
            pointer.initialize(to: v)
            CFBinaryHeapAddValue(minHeap, pointer)
        }

        for _ in 0..<extraStudents
        {
            let v = CFBinaryHeapGetMinimum(minHeap).load(as: [Int].self)
            CFBinaryHeapRemoveMinimumValue(minHeap)
            pointer = .allocate(capacity: 1)
            pointer.initialize(to: [v[0]+1, v[1]+1])
            CFBinaryHeapAddValue(minHeap, pointer)
        }

        var res: Double = 0
        for _ in 0..<classes.count
        {
            let v = CFBinaryHeapGetMinimum(minHeap).load(as: [Int].self)
            CFBinaryHeapRemoveMinimumValue(minHeap)
            res += Double(v[0])/Double(v[1])
        }
        pointer.deallocate()
        return res/Double(classes.count)
    }
}

private extension CFBinaryHeap
{
    static func minHeap() -> CFBinaryHeap
    {
        var callbacks = CFBinaryHeapCallBacks()
        callbacks.compare = { a, b, _ in
            let (x, y) = (a!.load(as: [Int].self), b!.load(as: [Int].self))
            let l = Double(x[0]+1)/Double(x[1]+1)-Double(x[0])/Double(x[1])
            let r = Double(y[0]+1)/Double(y[1]+1)-Double(y[0])/Double(y[1])
            return l == r ? 0 : l > r ? -1 : 1
        }
        return CFBinaryHeapCreate(nil, 0, &callbacks, nil)
    }
}
