```````
class Solution {
    /*Keep a max heap of the current class sizes and order them by the change in pass ratio. For each extra student, take the top of the heap, update the class size, and put it back in the heap.*/
    public double maxAverageRatio(int[][] classes, int extraStudents) {
        PriorityQueue<int[]> pq = new PriorityQueue<int[]>( 
            (a, b) -> {
                double ch1 = (a[0]+1)*1.0/(a[1]+1) - (a[0])*1.0/(a[1]);
                double ch2 = (b[0]+1)*1.0/(b[1]+1) - (b[0])*1.0/(b[1]);
                return Double.compare(ch2, ch1); 
            } 
        );

        for (int[] c : classes) {
            pq.offer(c);
        }
        while (extraStudents > 0) {
            int[] cur = pq.poll();
            pq.offer(new int[] {cur[0]+1, cur[1]+1});
            extraStudents--;
        }
        double sum = 0.0;
        while (!pq.isEmpty()) {
            int[] cur = pq.poll();
            sum += (cur[0])*1.0/(cur[1]);
        }
        return sum/classes.length;
    }
}
