var maxAverageRatio = function(classes, extraStudents) {
    const priorityQueue = new MaxPriorityQueue();
    let index;

    const getPriority = c => {
        return Math.floor(((c[0] + 1) / (c[1] + 1) - c[0] / c[1]) * 1e10);
    };

    for (let i = 0; i < classes.length; ++i) {
        if (classes[i][0] === classes[i][1]) continue;
        priorityQueue.enqueue(i, getPriority(classes[i]));
    }

    if (priorityQueue.size() > 0) {
        for (let i = 0; i < extraStudents; ++i) {
            index = priorityQueue.dequeue().element;
            ++classes[index][0];
            ++classes[index][1];
            priorityQueue.enqueue(index, getPriority(classes[index]));
        }
    }

    let result = 0;
    for (let i = 0; i < classes.length; ++i) {
        result += classes[i][0] / classes[i][1];
    }

    return result / classes.length;
};
