class Solution {
    public double maxAverageRatio(int[][] classes, int extraStudents) {
        int n = classes.length;

        // Create priority queue to store the data, see comparator class
        PriorityQueue<Classroom> cpq = new PriorityQueue<Classroom>(n, new CustomComparator()); 
        int i;
        double ans = 0.0;

        for(i = 0; i < n; i++) {
            cpq.add(new Classroom(classes[i][0], classes[i][1]));
        }

        for(i = 0; i < extraStudents; i++) {

            // In our comparator, we have already calculated which is the best class to put that extra student
            // So this if function is just updating the priority queue with the latest calculated pass ratio
            Classroom c = cpq.poll();
            cpq.add(new Classroom(c.pass + 1, c.total + 1));
        }

        for(i = 0; i < n; i++) {
            Classroom c = cpq.poll();
            ans += c.pr;
        }

        return ans / n;

    }
}

class Classroom {
    double pass;
    double total;
    double pr;
    public Classroom(double p, double t) {
        pass = p;
        total = t;
        pr = p / t;
    }
}

class CustomComparator implements Comparator<Classroom> {

    @Override
    public int compare(Classroom c1, Classroom c2) {

        // We want to maximize our pass ratio, so putting the extra student in whichever class
        // gives us better profit in pass ratio wins.
        double pr1 = ((c1.pass + 1) / (c1.total + 1)) - c1.pr;
        double pr2 = ((c2.pass + 1) / (c2.total + 1)) - c2.pr;
        if (pr1 > pr2) {
            return -1;
        }
        else if (pr1 < pr2) {
            return 1;
        }
        else {
            return 0;
        }
    }
}
