class Solution {
    public double maxAverageRatio(int[][] classes, int extraStudents) {
        PriorityQueue<double[]> pq = new PriorityQueue<>((double[] v1, double[] v2) -> Double.compare(v2[2], v1[2]));
        for (int[] c : classes) {
            pq.add(new double[]{c[0],c[1], ((double)(c[0]+1)/(c[1]+1) - (double)c[0]/c[1])});
        }

        for (int i = 0; i < extraStudents; i++) {
            double[] next = pq.poll();
            next[0]++;
            next[1]++;
            next[2] = ((double)(next[0]+1)/(next[1]+1) - (double)next[0]/next[1]);
            pq.add(next);
        }

        double res = 0;
        while ( ! pq.isEmpty()) {
            res += (double)pq.peek()[0]/pq.poll()[1];
        }

        return res/classes.length;
    }
}
