class Solution {
public:
    double maxAverageRatio(vector<vector<int>>& nums, int k) {
        double sum = 0;
        double n = nums.size();

        priority_queue<pair<double,pair<double,double>>> q;

        vector<vector<double>> arr(n);
        for(int i=0 ; i<n ; i++){
            vector<double> temp(2);
            temp[0] = nums[i][0];
            temp[1] = nums[i][1];
            arr[i] = temp;
        }



        for(int i=0 ; i<n ; i++){
            q.push({((arr[i][0]+1)/(arr[i][1]+1))- (arr[i][0]/arr[i][1]), 
            {arr[i][0] , arr[i][1]}});
        }

        while(k--){
           double x = q.top().second.first;
           double y = q.top().second.second;
           q.pop();
           x = x+1;
           y = y+1;
           q.push({((x+1)/(y+1))-(x/y) , {x , y}});
        }


        while(!q.empty()){
            double x = q.top().second.first;
            double y = q.top().second.second;
            sum += x/y;
            q.pop();
        }


        return sum/n;
    }
};
