            pq.push({(p+1)/(t+1) - p/t, p, t}) ;
        }

        double sum  = 0 ;
        while(!pq.empty())
        {
            auto[dr, p, t] = pq.top() ;
            pq.pop() ;
            sum += p/t ;
        }

        return sum / n ;
    }

