	class Solution {
    public class pair{
        double ratio; int num, den;

        pair(double r, int n, int d){
            this.ratio=r;
            this.num=n;
            this.den=d;
        }
    }
    public double maxAverageRatio(int[][] classes, int es) {
        PriorityQueue<pair>pq=new PriorityQueue<>(
            (a, b)->{
                //between two pairs, in which will there be more progress in ratio?
                double newratioa=(a.num+1)*1.0/(a.den+1);
                double newratiob=(b.num+1)*1.0/(b.den+1);
                double diffa=newratioa-a.ratio;
                double diffb=newratiob-b.ratio;

                if(diffa>diffb) return -1;
                else return 1;
        });

        for(int i[]: classes)
        {
            double ratio=i[0]*1.0/(i[1]);
            pair curr=new pair(ratio, i[0], i[1]);
            pq.add(curr);
        }

        while(es>0)
        {
            es--;
            pair kid=pq.remove();
            //System.out.println(kid.num+" "+kid.den);
            int num=kid.num+1; int den=kid.den+1;
            double ratio=num*1.0/den;

            pq.add(new pair(ratio, num, den));
            //System.out.println(ratio+" "+num+" "+den);
        }

        double res=0;int n=pq.size();

        while(pq.size()>0)
        {
            res+=pq.remove().ratio;
        }
        return res/n;
    }
}

