    def maxAverageRatio(self, classes: List[List[int]], extraStudents: int) -> float:
        profit = lambda passedStudents, totalStudents: (passedStudents + 1) / (totalStudents + 1) - passedStudents / totalStudents
        total, maxHeap = sum(c[0] / c[1] for c in classes), []
        for passedStudents, totalStudents in classes:
