class Solution {
    public double maxAverageRatio(int[][] classes, int extraStudents) {
        double sum = 0;
        PriorityQueue<pair> pq = new PriorityQueue<>((a,b) -> {
            double av1 = (a.pass+1)/(a.total+1) - (a.pass/a.total);
            double av2 = (b.pass+1)/(b.total+1) - (b.pass/b.total);
            if(av1 == av2){
                return 0;
            }
            return (av1 > av2)?-1:1;
        });
        for(int temp[] : classes){
            pair p = new pair(temp[0], temp[1]);
            pq.offer(p);
        }
        for(int i = 0;i<extraStudents;i++){
            double temp1 = pq.peek().pass;
            double temp2 = pq.poll().total;
            temp1 += 1;
            temp2 += 1;
            pq.offer(new pair(temp1, temp2));
        }
        while(!pq.isEmpty()){
            sum += pq.peek().pass/pq.poll().total;
        }
        return sum/classes.length;
    }
}
