			for(int i =0;i<extraStudents;i++){
				double[] curr = pq.poll();
				pq.add(new double[]{curr[0]+1,curr[1]+1});
			}

			double sum = 0;
			while(!pq.isEmpty()){
				double[] curr = pq.poll();
				sum+=curr[0]/curr[1];
			}

			return sum/classes.length;
		}
