class Solution {
    public double maxAverageRatio(int[][] classes, int extraStudents) {
        double res=0;

        Queue<Class> queue=new PriorityQueue<>((a,b)->b.diff.compareTo(a.diff));

        queue.addAll(IntStream
                    .range(0,classes.length) 
                    .parallel()
                    .mapToObj(e->new Class(classes[e][0],classes[e][1]))
                    .collect(Collectors.toList()));


        while(extraStudents>0)
        {          
            //update the first which had the big new ratio
            Class c=queue.poll(); 
            c.updateClass(1); 
            queue.add(c); 
            extraStudents--;
        }
        while(!queue.isEmpty())
        {
            Class c=queue.poll(); 
            res+=c.ratio;
        }

        return res/Double.valueOf(classes.length);
    }
    class Class{
        public int students;
        public int pass;
        public Double ratio;
        public Double newRatio;
        public Double diff=0.0;
        public Class(int pass, int students)
        {
            this.students=students;
            this.pass=pass;
            this.ratio=Double.valueOf(this.pass)/Double.valueOf(this.students);
             this.calcNewRatio(1);
        }

        public void updateClass(int extStudent)
        {
            this.students+=extStudent;
            this.pass+=extStudent;
            this.ratio=Double.valueOf(this.pass)/Double.valueOf(this.students);
            this.calcNewRatio(extStudent);
        }

        public void calcNewRatio(int extStudent)
        {
            this.newRatio=Double.valueOf(this.pass+extStudent)/Double.valueOf(this.students+extStudent);
            this.diff=this.newRatio-this.ratio;
        }
    }
}


