class Solution {

    struct Class {
        int pass;
        int total;
        double ratio() const { return double(pass) / double(total); }
        double order() const { return double(pass + 1) / double(total + 1) - ratio(); }
    };

    struct pass_compare {
        bool operator()(const Class& lhs, const Class& rhs) {
            return lhs.order() < rhs.order();
        }
    };

public:
    double maxAverageRatio(vector<vector<int>>& classes, int extraStudents) {
        priority_queue<Class, vector<Class>, pass_compare> ratio(pass_compare{});
        int full = 0;
        for (auto& c : classes) {
            int p = c[0];
            int t = c[1];
            ratio.push(Class{p, t});
        }

        while (extraStudents--) {
            auto temp = ratio.top();
            ratio.pop();
            ratio.push(Class{temp.pass + 1, temp.total + 1});
        }

        double res = 0;
        while (!ratio.empty()) {
            res += ratio.top().ratio();
            ratio.pop();
        }
        return (res + full) / classes.size();
    }
};
