double maxAverageRatio(vector<vector<int>>& classes, int extraStudents) {
	priority_queue<pair<double,int>> pq;
	double rate;
	for(int i = 0; i < classes.size(); i++) {
		rate = ((double) classes[i][0]+1)/((double)classes[i][1]+1) - ((double)classes[i][0]/(double)classes[i][1]);
		pq.push({rate,i});
	}

	int cl;
	for(int i = 0; i < extraStudents; i++) {
		cl = pq.top().second, pq.pop();
		classes[cl][0]++, classes[cl][1]++;
		rate = ((double)classes[cl][0]+1)/((double)classes[cl][1]+1) - (double)classes[cl][0]/classes[cl][1];
		pq.push({rate,cl});
	}

	rate = 0;
	for(auto i : classes) {
		rate += (double)i[0]/i[1];
	}
	rate/=classes.size();
	return rate;
}
