class Solution {
    class P implements Comparable<P>{
        double a;
        double b;
        P(){}
        P(double a, double b){
            this.a=a;
            this.b=b;
        }
        public int compareTo(P p){
            //compare only increased ratio
            double t1 = ((this.a+1)/(this.b+1)) - (this.a/this.b);
            double t2 = ((p.a+1)/(p.b+1)) -(p.a/p.b);
            if(t1<t2) return 1;
            else if(t1>t2) return -1;
            else
                return 0;
        }
    }
    public double maxAverageRatio(int[][] classes, int extraStudents) {
        Queue<P> q = new PriorityQueue<P>();
        for(int i=0;i<classes.length; i++){
            q.add(new P(classes[i][0], classes[i][1]));

        }
        while(extraStudents-- > 0){
            P p = q.remove();
            p.a+=1;
            p.b+=1;
            q.add(p);
        }
        double sum =0.0;
        for( P p : q){
            sum+=(p.a/p.b);
        }
        return sum/classes.length;
    }
}
