class Solution {
    public double maxAverageRatio(int[][] classes, int extraStudents) {
        PriorityQueue<Course> pq=new PriorityQueue<Course>(new Comparator<Course>(){
            @Override
            public int compare(Course a, Course b){
                if(a.potIncrease>b.potIncrease){
                    return -1;
                }
                return 1;
            }
        });

        for(int[] cls: classes){
            int num=cls[0];
            int deno=cls[1];
            double currRat=(double)(num)/(double)deno;
            double next=(double)(num+1)/(double)(deno+1);
            pq.add(new Course(num, deno, next-currRat));
        }

        while(extraStudents>0){
            Course curr=pq.poll();
            curr.num+=1;
            curr.deno+=1;
            double currRat=(double)(curr.num)/(double)curr.deno;
            double next=(double)(curr.num+1)/(double)(curr.deno+1);
            curr.potIncrease=next-currRat;
            pq.add(curr);
            extraStudents--;
        }

        double ans=0;
        while(pq.size()>0){
            Course curr=pq.poll();
            ans+=(double)(curr.num)/(double)curr.deno;
        }
        return ans/(double)classes.length;        
    }


    class Course{
        int num, deno;
        double potIncrease;
        Course(int num, int deno, double potIncrease){
            this.num=num;
            this.deno=deno;
            this.potIncrease=potIncrease;
        }
    }
}
