import scala.collection.mutable

object Solution {
  private implicit val ClassOrdering: Ordering[Array[Int]] = Ordering.by {
    case Array(pass, total) => (total - pass).toDouble / total / (total + 1)
  }

  def maxAverageRatio(classes: Array[Array[Int]], extraStudents: Int): Double = {
    val queue = classes.to(mutable.PriorityQueue)

    for (_ <- 1 to extraStudents) queue.dequeue match {
      case Array(pass, total) => queue.enqueue(Array(pass + 1, total + 1))
    }

    queue.iterator.collect { case Array(pass, total) => pass.toDouble / total }.sum / classes.length
  }
}
