class Solution {
public:
    double helper(vector<vector<int>> &classes, int curr, int K){
        if(K==0){  // if k=0 achieved, rest all from curr till n are just gonna add their averages
            double average=0.0;
            for(int i=curr;i<classes.size();i++){
                average=average+(((double)classes[i][0])/((double)classes[i][1]));
            }
            return average;
        }

        if(curr==classes.size()){  // if k is not zero and curr reached end, return min
            return (double)INT_MIN;
        }

        double res=(double)INT_MIN;

        for(int i=0;i<=K;i++){ 
		/* trying all possible k, giving 0 brillant student to the current class
			to giving all brilliant student to this class */
            double tempAvg=(((double)classes[curr][0]+i)/((double)classes[curr][1]+i))+helper(classes,curr+1,K-i);
            res=max(res,tempAvg);
        }

        return res;
    }
    double maxAverageRatio(vector<vector<int>>& classes, int extraStudents) {
        return helper(classes,0,extraStudents)/classes.size();
    }
};
```

