class Solution {
public:

    struct cmp{
        bool operator()(pair<int,int> &p1, pair<int,int> &p2){
            double diff1=((double)(p1.first+1.0)/(double)(p1.second+1.0))-((double)(p1.first)/(double)(p1.second));
            double diff2=((double)(p2.first+1.0)/(double)(p2.second+1.0))-((double)(p2.first)/(double)(p2.second));
            return diff1<diff2; // the top of heap will contain a class where the difference is max
        }
    };
    double maxAverageRatio(vector<vector<int>>& classes, int extraStudents) {
        priority_queue<pair<int,int>,vector<pair<int,int>>,cmp> pq;
        for(auto klass: classes){
            pq.push({klass[0],klass[1]});
        }
        while(extraStudents--){
            auto tp=pq.top();
            pq.pop();
            tp.first++;
            tp.second++;
            pq.push(tp);
        }
        double res=0.0;
        while(!pq.empty()){
            auto tp=pq.top();
            pq.pop();
            res+=(((double)tp.first)/((double)tp.second));
        }
        return res/classes.size();
    }
};
