class Solution {
    public double profit(double a,double b)
    {
        return ((a+1)/(b+1))-(a/b);
    }
    public double maxAverageRatio(int[][] classes, int extraStudents) {
        PriorityQueue<double[]>pq=new PriorityQueue<double[]>(new Comparator<double[]>(){
            public int compare(double[] a1,double[] a2)
            {
                return Double.compare(a2[0],a1[0]);
            }
        });
        for(int[] clas:classes)
        {
            double delta=profit(clas[0],clas[1]);
            pq.offer(new double[]{delta,clas[0],clas[1]});
        }
        while(extraStudents>=1)
        {
            double[] temp=pq.poll();
            double pass=temp[1]+1;
            double total=temp[2]+1;
            double delta=profit(pass,total);
            pq.offer(new double[]{delta,pass,total});
            extraStudents--;
        }
        double avg=0.0;
        while(!pq.isEmpty())
        {
            double[] temp=pq.poll();
            avg+=temp[1]/temp[2];
        }
        return avg/classes.length;
    }
}
