        };

        priority_queue<Ratio, vector<Ratio>, decltype(cmp)> pq(cmp);

        for (int i = 0; i <classes.size(); ++i) {
            if (classes[i][0] == classes[i][1]) {
                ratio += 1;
            } else {
                pq.push({classes[i][0], classes[i][1]});
