        while (extraStudents--) {
            Ratio p = pq.top();
            pq.pop();
            pq.push({p.first + 1, p.second + 1});
        }

        while (!pq.empty()) {
            ratio += pq.top().first / (double)pq.top().second;
            pq.pop();
        }

        return ratio / classes.size();
    }
};
