class Solution {
private:
    typedef pair<int, int> Class; // {pass : total student}

    struct CmpClass {
    public:
        bool operator() (Class &c1, Class &c2) {
            return (double)(c1.first + 1) / (double)(c1.second + 1) - (double)c1.first / (double)c1.second <
                   (double)(c2.first + 1) / (double)(c2.second + 1) - (double)c2.first / (double)c2.second;
        }
    };
public:
    double maxAverageRatio(vector<vector<int>>& classes, int extraStudents) {
        double totalAvgSum = 0;
        priority_queue<Class, vector<Class>, CmpClass> priQ;

        for (vector<int> &eachC : classes) {
            if (eachC[0] == eachC[1]) {
               totalAvgSum += (double)1;
                continue;
            }
            priQ.emplace(eachC[0], eachC[1]);
        }

        while (!priQ.empty() && extraStudents-- > 0) {
            int nextPass = priQ.top().first + 1;
            int nextTotal = priQ.top().second + 1;
            priQ.pop();
            priQ.emplace(nextPass, nextTotal);
        }

        while (!priQ.empty()) {
            totalAvgSum += (double)priQ.top().first / (double)priQ.top().second;
            priQ.pop();
        }

        return totalAvgSum / classes.size();
    }
};
