             double a = c[0], b = c[1];
             set.add(new double[]{profit(a,b),a,b});
         }

         while(extraStudents-- > 0){
             double[] arr = set.pollFirst();
             double a = arr[1];
             double b = arr[2];
             set.add(new double[]{profit(a+1,b+1),a+1,b+1});
         }
         double ans = 0.0d;
         while(!set.isEmpty()){
             double[] arr = set.pollFirst();
             double a=arr[1],b=arr[2];
             ans += a/b;
         }

         return (double)(ans/classes.length);


    }
    public double profit(double a, double b){
        return (a + 1) / (b + 1) - a / b;
    }
}

