class Solution {
public:
    int sumSubarrayMins(vector<int>& arr) {
        int n=arr.size();
        long long ans=0;
        int m=1000000007;
        vector<long long>nsr(n),nsl(n);
        //NSL
        stack<int>st;
        for(int i=0;i<n;i++){
            while(!st.empty() && arr[st.top()]>=arr[i])
                st.pop();
            nsl[i]=st.empty()?-1:st.top();
            st.push(i);
        }
        //NSR
        stack<int>tt;
        for(int i=n-1;i>=0;i--){
            while(!tt.empty() && arr[tt.top()]>arr[i])
                tt.pop();
            nsr[i]=tt.empty()?-1:tt.top();
            tt.push(i);
        }
        for(int i=0;i<n;i++){
            long long l= (nsl[i]==-1 ? i+1:i-nsl[i]);
            long long r= (nsr[i]==-1 ? n-i:-i+nsr[i]);
            ans+=(arr[i]*l*r);
